import './App.css';
import * as THREE from 'three'
import React, { useRef, Component, useEffect } from 'react'
import { Canvas, useLoader } from 'react-three-fiber'
import { OrbitControls } from 'drei'
import { useBox, usePlane, Physics } from 'use-cannon'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
import SphereBot from './assets/sphere_bot/scene.gltf'
import { RotCube } from './Cube'


function useKey(key, cb){
  const callbackRef = useRef(cb)
  useEffect(()=>{
    callbackRef.current = cb
  })

  useEffect(()=>{
    function handle(event){
      if(event.code ===key){
        callbackRef.current(event)
      }
    }
    document.addEventListener('keypress', handle)
    return () => document.removeEventListener('keypress', handle)
  }, [key])
}


function Cube(props) {
  const [ref, api] = useBox(() => ({ mass: 1, position: [0, 5, 0], ...props }))
  const mesh = useRef(null)


  return (
    <mesh 
      onClick={()=>{
      // api.velocity.set(0,5,0)

      api.velocity.set(20*(Math.random()-0.5),0,20*(Math.random()-0.5))

      }} 
      ref={ref}>
      <boxBufferGeometry attach="geometry" />
      <meshStandardMaterial attach='material' color='#50c878' />
    </mesh>
  )
}

function Bot() {
  const gltf = useLoader(GLTFLoader, SphereBot)
  return <primitive object={gltf.scene} position={[0, 5, 0]} />
}

