import React, { useState, useEffect, useRef } from 'react'
import { useSpring } from '@react-spring/core'
import { a } from "@react-spring/three"
import { useBox } from 'use-cannon'


export const RotCube = ({ position }, props) => {
  const [ active, setActive] = useState(0)
  const activeRef = useRef(active)
  activeRef.current = active

  const mesh = useRef(null)

  useEffect(() => {
    let timeout
    const toggleActive = () => {
      timeout = setTimeout(() => {
        setActive(Number(!activeRef.current))
        toggleActive()
      }, Math.random() * 2000 + 1000)
    }
    toggleActive()
    return () => {
      clearTimeout(timeout)
    }
  }, [])

  const { spring } = useSpring({
    spring: active,
    config: { mass: 5, tension: 400, friction: 50, precision: 0.0001 }
  })

  const scale = spring.to([0, 1], [1, 2])
  const rotation = spring.to([0, 1], [0, Math.PI])
  const color = spring.to([0, 1], ["#50c878", "#e45858"])


  const [ref, api] = useBox(() => ({ mass: 1, position: position, rotation:[Math.random()*10-5,Math.random()*10-5,Math.random()*10-5], ...props }))
  return (

    <a.mesh ref={ref}
        onClick={()=>{
            // api.velocity.set(0,5,0)
            if(!active)
                api.velocity.set(20*(Math.random()-0.5),0,20*(Math.random()-0.5))
            }} 
      rotation={[0,rotation,0]}
      position={position}
    >
      <boxBufferGeometry attach="geometry" args={[1, 1, 1]}/>
      <a.meshStandardMaterial roughness={0.5} attach="material" color={color}/>
    </a.mesh>
  )
}
