import './App.css';
import * as THREE from 'three'
import React, { useRef, Component } from 'react'
import { Canvas, useLoader } from 'react-three-fiber'
import { OrbitControls } from 'drei'
import { useBox, usePlane, Physics } from 'use-cannon'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
import SphereBot from './assets/sphere_bot/scene.gltf'




function Cube(props) {
  const [ref, api] = useBox(() => ({ mass: 1, position: [0, 5, 0], ...props }))
  const mesh = useRef(null)
  // useFrame(()=>(mesh.current.rotation.x = mesh.current.rotation.y += 0.01))
  return (
    <mesh 

      onClick={()=>{
      // api.velocity.set(0,5,0)
      api.velocity.set(5,0,5)
      }} 
      ref={ref}>
      <boxBufferGeometry attach="geometry" />
      <meshStandardMaterial attach='material' color='green' />
    </mesh>
  )
}

function Bot() {
  const gltf = useLoader(GLTFLoader, SphereBot)
  return <primitive object={gltf.scene} position={[0, 5, 0]} />
}

function App() {

  componentDidMount = () => {
    document.onkeydown = this.onKeyDown;
  }

  onKeyDown = (e) => {
    e = e || window.event;
    switch (e.keyCode) {
      case 38:
        this.setState({direction: 'UP'});
        break;
      case 40:
        this.setState({direction: 'DOWN'});
        break;
      case 37:
        this.setState({direction: 'LEFT'});
        break;
      case 39:
        this.setState({direction: 'RIGHT'});
        break;
    }
  }

  function Plane(props) {
    const [ref] = usePlane(() => ({ rotation: [-Math.PI / 2, 0, 0], ...props }))
    return (
      <mesh ref={ref}>
        <planeBufferGeometry attach="geometry" args={[200, 200]} />
        <meshStandardMaterial attach='material' color='linear-gradient(rgba(250,0,0,0.5),transparent)' />
      </mesh>
    )
  }




  return (
    <>
      <Canvas camera={{ position: [5, 5, 5], fov: 65 }}
        onCreated={({ gl, scene }) => {
          scene.background = new THREE.Color('lightblue');
          gl.shadowMap.enabled = true;
          gl.shadowMap.type = THREE.PCFSoftShadowMap;
        }}>
      >
        <OrbitControls />
        <ambientLight intensity={0.5} />
        <spotLight 
          intensity={0.5}
          position={[10,15,10]}
          angle={0.3}
        />
        <Physics>
          <Plane />

          {/* <Bot /> */}
          <Cube />
        </Physics>
      </Canvas>
    </>
  );
}

export default App;
